# prevent posts that contain some plantbased meat from being skipped
ignore_list = ['vegan', 'veggie', 'plantbased']


def next_to_unfollow(amount):
    delta = datetime.now().date() - unfollow_start_date
    day_count = delta.days

    lim_inf = day_count * amount
    lim_sup = day_count * amount + amount

    next_list = unfollow[lim_inf:lim_sup]

    return next_list
